// client/src/features/cars/CarList.js

import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";
import CarForm from "features/cars/components/CarForm";
import ItemCardList from "components/shared/ItemCardList";
import ItemExtras from "components/shared/ItemExtras";
import carSchema from "features/cars/carSchema";
// Filtered view
import statusLabels from "helpers/statusLabels";
import {
  getStatusFilterOptions,
  filterByStatus,
  getStatusLabel,
} from "helpers/filterUtils";

function CarList() {
  const [cars, setCars] = useState(() => {
    const saved = localStorage.getItem("cars");
    return saved ? JSON.parse(saved) : [];
  });

  const [formData, setFormData] = useState({});
  const [showForm, setShowForm] = useState(false);
  const [editIndex, setEditIndex] = useState(null);
  const [filterStatus, setFilterStatus] = useState("all");

  useEffect(() => {
    localStorage.setItem("cars", JSON.stringify(cars));
  }, [cars]);

  const handleAddCar = (e) => {
    e.preventDefault();

    if (editIndex !== null) {
      setCars((prev) =>
        prev.map((car, i) => (i === editIndex ? formData : car))
      );
      setEditIndex(null);
    } else {
      setCars([...cars, formData]);
    }

    setFormData({});
    setShowForm(false);
  };

  const startEditing = (index) => {
    setFormData(cars[index]);
    setEditIndex(index);
    setShowForm(true);
  };

  const deleteCar = (index) => {
    setCars((prev) => prev.filter((_, i) => i !== index));
    if (editIndex === index) {
      setFormData({});
      setEditIndex(null);
      setShowForm(false);
    }
  };

  const carStatuses = getStatusFilterOptions("cars");
  const filteredCars = filterByStatus(cars, filterStatus);
  const sectionTitle = `Cars - ${getStatusLabel("cars", filterStatus)}`;

  return (
    <>
      {!showForm && (
        <Button
          variant="primary"
          className="mb-3"
          onClick={() => setShowForm(true)}
        >
          Add Car
        </Button>
      )}

      {showForm && (
        <CarForm
          formData={formData}
          setFormData={setFormData}
          onSubmit={handleAddCar}
        />
      )}

      <div className="mb-3">
        <label>Status Filter: </label>
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
        >
          {carStatuses.map((status) => (
            <option key={status} value={status}>
              {status === "all" ? "All" : statusLabels.cars?.[status] || status}
            </option>
          ))}
        </select>
      </div>

      <ItemCardList
        title={sectionTitle}
        items={filteredCars}
        schema={carSchema}
        onEdit={startEditing}
        onDelete={deleteCar}
        renderItemExtras={(item) => <ItemExtras item={item} />}
      />
    </>
  );
}

export default CarList;
